mapper.select:
SELECT e.emp_no prfNo, e.emp_name prfName, e.dept_no deptNo, IFNULL(d.dept_name, '미배정') deptName, e.emp_email prfEmail, IFNULL(e.emp_phone, ' ') prfPhone
FROM emp e LEFT OUTER JOIN (SELECT dept_no, dept_name FROM dept) d ON e.dept_no = d.dept_no
WHERE e.emp_role = 2

	AND e.emp_name LIKE CONCAT('%', #{searchWord}, '%')


	AND e.dept_no = #{searchCategory}

ORDER BY e.emp_no DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(e.emp_no) FROM emp e LEFT OUTER JOIN (SELECT dept_no, dept_name FROM dept) d ON e.dept_no = d.dept_no WHERE e.emp_role = 2
AND e.emp_name LIKE CONCAT('%', #{searchWord}, '%')
AND e.dept_no = #{searchCategory}

mapper.update:
	UPDATE emp
    SET  emp_name = #{prfName}, emp_email = #{prfEmail}, dept_no = #{deptNo}, emp_phone = #{prfPhone}
    	, emp_birth = #{prfBirth}, emp_img = #{prfImg}
    WHERE emp_no = #{prfNo}

mapper.select:
SELECT s.student_no studentNo, s.student_name studentName, s.dept_no deptNo, IFNULL(d.dept_name, '') deptName, s.student_email studentEmail
, IFNULL(s.student_phone, '') studentPhone, s.student_state studentState
FROM student s LEFT OUTER JOIN (SELECT dept_no, dept_name FROM dept) d ON s.dept_no = d.dept_no


		AND s.student_name LIKE CONCAT('%', #{searchWord}, '%')


		AND s.dept_no = #{searchCategory}


ORDER BY s.student_no ASC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(s.student_no) FROM student s LEFT OUTER JOIN (SELECT dept_no, dept_name FROM dept) d ON s.dept_no = d.dept_no
AND s.student_name LIKE CONCAT('%', #{searchWord}, '%')
AND s.dept_no = #{searchCategory}

mapper.update:
	UPDATE student
    SET  student_name = #{studentName}, student_email = #{studentEmail}, dept_no = #{deptNo}, student_phone = #{studentPhone}
    	, student_birth = #{studentBirth}, student_img = #{studentImg}, student_state = #{studentState}
    WHERE student_no = #{studentNo}
