mapper.update:
UPDATE student
student_pw = #{studentPw},
student_name = #{studentName}, student_state = #{studentState}, student_address = #{studentAddress}, student_phone = #{studentPhone}, student_birth = #{studentBirth}, student_img = #{studentImg}, student_email = #{studentEmail}
WHERE student_no = #{studentNo}

mapper.select:
SELECT COUNT(*) 
FROM registration
WHERE student_no = #{studentNo} AND course_no = #{courseNo}

SELECT course_no
FROM project
WHERE project_no = #{projectNo}

SELECT emp_no
FROM course
WHERE course_no = #{courseNo}

SELECT course_name
FROM course
WHERE course_no = #{courseNo}

mapper.update:
UPDATE question
SET
    question_title = #{questionTitle},
    question_contents = #{questionContents}
WHERE
    question_no = #{questionNo}
    AND student_no = #{studentNo}
